public class Java_PI {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = (int) (4 * (1.0 - 1.0/3 + 1.0/5 - 1.0/7 + 1.0/9 - 1.0/11));
		System.out.println(result);
		int result2 = (int) (4*(1.0 - 1.0/3 + 1.0/5 - 1.0/7 + 1.0/9 - 1.0/11 +1.0/13));
		System.out.println(result2);
		System.out.println("The two results of Pi are " + result + " and " + result2);
	}

}
